Source code in <code>src/pytanis/config.py</code> <pre><code>@field_validator('Google')
@classmethod
def convert_json_path(cls, v: GoogleCfg | None, info: ValidationInfo) -&gt; GoogleCfg | None:
    if v is None:
        return v

    def make_rel_path_abs(entry):
        if entry is not None and not entry.is_absolute():
            entry = info.data['cfg_path'].parent / entry
        return entry

    v.client_secret_json = make_rel_path_abs(v.client_secret_json)
    v.token_json = make_rel_path_abs(v.token_json)

    return v
</code></pre>

